mapper.insert:

	select seq_detail_no.nextval from dual
 

	insert into detail 	(detail_no,
					prod_no
					)	
						
			values 	(#{detailNo},
					#{prodNo}
					)

mapper.update:

	update 	detail
	set 		prodpath = #{prodPath}
	where 	detail_no = #{detailNo}


	update 	detail
	set 		castingpath = #{castingPath}
	where 	detail_no = #{detailNo}


	update 	detail
	set 		addedpath = #{addedPath}
	where 	detail_no = #{detailNo}
