mapper.update:

	update reserve
	set totalPayment = #{totalPayment}
	where rez_no = #{rezNo}

mapper.select:

	select totalPayment
	from reserve
	where rez_no = #{rezNo}

select u.user_no userNo, r.rez_no rezNo, to_char(r.rezdate, 'yy-mm-dd hh24:mi') rezDate, r.prod_no prodNo, p.prodname prodName, to_char(r.viewdate, 'yy-mm-dd hh24:mi') viewDate, r.ticketCount ticketCount, r.status status
from (select r.rez_no, r.rezdate, r.viewdate, r.status, r.prod_no, count(s.selseat_no) ticketCount
from reserve r, selseat s where r.rez_no = s.rez_no group by r.rez_no, r.rezdate, r.viewdate, r.status, r.prod_no) r, product p, users u where u.user_no= #{userNo}
