mapper.select:

	select salt
	from users
	where id = #{id}

 
	select count(*)
	from users
	where id = #{id}

mapper.update:
update users set
password = #{password}, phone = #{phone}, postcode = #{postcode}, address = #{address}, address2 = #{address2}, email = #{email}, salt = #{salt}
where user_no = #{userNo}

mapper.delete:

		DELETE FROM users 
		WHERE		user_no = #{userNo}
		and			password = #{password}
